   # [q.v. script_create_kingdom_hero_party]
   (troop_set_slot, ":dead_troop", slot_troop_is_alive, 0),  ## he's dead/inactive
   (troop_set_slot, ":dead_troop", slot_troop_death_cause, 2),  ## executed
